                    "title": "A+B\u95ee\u9898\u76847\u79cd\u89e3\u6cd5",
                    "content": "<h2><strong>A+B \u95ee\u9898\u76847\u79cd\u89e3\u6cd5</strong></h2><h2>\u6700\u666e\u901a\u7248</h2><pre><code>#include &lt;iostream&gt;\n#include &lt;cstdio&gt;\n\nusing namespace std;\n\nint main() {\n    int a,b;\n    cin &gt;&gt; a &gt;&gt; b;\n    cout &lt;&lt; a+b;\n    return 0;\n}\n</code></pre><hr /><h2>\u4e8c\u5206\u7b97\u6cd5</h2><pre><code>#include&lt;cstdio&gt;\nusing namespace std;\nint a,b,c;\n int main(){long long l=-int(1e9)&lt;&lt;1,r=int(1e9)&lt;&lt;1;//\u5de6\u8fb9\u754c\u548c\u53f3\u8fb9\u754c\n     scanf(&quot;%d%d&quot;,&a,&b);\n     while(r-l&gt;1){c=(l+r)&gt;&gt;1;//\u4e8c\u5206\u7684\u6b65\u9aa4\u5566\n         if(c-b&lt;a)l=c;\n         else if(c-b&gt;a)r=c;\n             else return printf(&quot;%d\\n&quot;,c),0;\n     }if(l!=r)return printf(&quot;%d\\n&quot;,r),0;\n }\n</code></pre><hr /><h2>\u4f4d\u8fd0\u7b97</h2><pre><code>#include &lt;cstdio&gt;\nint m, n;\nint main()\n{\n    scanf(&quot;%d%d&quot;, &m, &n);\n    int u = m & n;\n    int v = m ^ n;\n    while (u) {\n        int s = v;\n        int t = u &lt;&lt; 1;\n        u = s & t;\n        v = s ^ t;\n    }\n    printf(&quot;%d\\n&quot;, v);\n}\n</code></pre><hr /><h2>\u6a21\u62df\u4eba\u5de5\u8fd0\u7b97</h2><pre><code>#include &lt;iostream&gt; \n#include &lt;cmath&gt;\nusing namespace std;\nint fu=1,f=1,a,b,c=0;\nint main()\n{\n    cin&gt;&gt;a&gt;&gt;b;\n    if(a&lt;0&&b&gt;0)fu=2;\n    if(a&gt;0&&b&lt;0)fu=3;\n    if(a&lt;0&&b&lt;0)f=-1;\n    if(a==0){cout&lt;&lt;b;return 0;}\n    if(b==0){cout&lt;&lt;a;return 0;} \n    a=abs(a);\n    b=abs(b);\n    if(a&gt;b&&fu==3)f=1;\n    if(b&gt;a&&fu==3)f=-1;\n    if(b&gt;a&&fu==2)f=1;\n    if(b&lt;a&&fu==2)f=-1;\n    if(fu==1)c=a+b;\n    if(fu&gt;1)c=max(a,b)-min(a,b);\n    c*=f;\n    cout&lt;&lt;c;\n    return 0;\n}\n</code></pre><hr /><h2>\u6811\u72b6\u6570\u7ec4</h2><pre><code>#include&lt;iostream&gt;\n#include&lt;cstring&gt;\nusing namespace std;\nint lowbit(int a)\n{\n    return a&(-a);\n}\nint main()\n{\n    int n=2,m=1;\n    int ans[m+1];\n    int a[n+1],c[n+1],s[n+1];\n    int o=0;\n    memset(c,0,sizeof(c));\n    s[0]=0;\n    for(int i=1;i&lt;=n;i++)\n    {\n        cin&gt;&gt;a[i];\n        s[i]=s[i-1]+a[i];\n        c[i]=s[i]-s[i-lowbit(i)];//\u6811\u72b6\u6570\u7ec4\u521b\u5efa\u524d\u7f00\u548c\u4f18\u5316\n    }\n    for(int i=1;i&lt;=m;i++)\n    {\n        int q=2;\n        {\n            int x=1,y=2;//\u6c42a[1]+a[2]\u7684\u548c\n            int s1=0,s2=0,p=x-1;\n            while(p&gt;0)\n            {\n                s1+=c[p];\n                p-=lowbit(p);//\u6811\u72b6\u6570\u7ec4\u6c42\u548c\u64cd\u4f5c\uff0c\u7528\u4e24\u4e2a\u524d\u7f00\u548c\u76f8\u51cf\u5f97\u5230\u533a\u95f4\u548c\n            }\n            p=y;\n            while(p&gt;0)\n            {\n                s2+=c[p];\n                p-=lowbit(p);\n            }    \n            o++;\n            ans[o]=s2-s1;//\u5b58\u50a8\u7b54\u6848\n        }\n    }\n    for(int i=1;i&lt;=o;i++)\n        cout&lt;&lt;ans[i]&lt;&lt;endl;//\u8f93\u51fa\n    return 0;\n}\n</code></pre><hr /><h2>floyd\u7b97\u6cd5</h2><pre><code>#include &lt;cstdio&gt;\nconst int N=5,oo=1023741823;\nint f[N][N];\ninline int mn(int a,int b){\n    return a&lt;b?a:b;\n}\nvoid floyd() {//floyd\u6a21\u677f\n    for (int k=1; k&lt;=N; k++) \n        for (int i=1; i&lt;=N; i++) if (i==k) continue;\n        else for (int j=1; j&lt;=N; j++) if (k==j||i==j) continue;\n        else f[i][j]=mn(f[i][j],f[i][k]+f[k][j]);\n}\nint main(){\n    int a,b;\n    for (int i=1;i&lt;=N;i++)\n        for (int j=1;j&lt;=N;j++)\n            f[i][j]=oo;\n    scanf (&quot;%d %d&quot;,&a,&b);\n    f[1][2]=a;\n    f[2][3]=b;//\u6784\u56fe\uff0c1-&gt;2\u7684\u6700\u77ed\u8def\u5f84\u662fa\uff0c2-&gt;3\u7684\u6700\u77ed\u8def\u5f84\u662fb\uff0c\u90a3\u4e481-&gt;3\u7684\u6700\u77ed\u8def\u5c31\u662fa+b\n    floyd();\n    printf (&quot;%d&quot;,f[1][3]);//\u8f93\u51fa\n    return 0;\n}\n</code></pre><hr /><h2>Link-Cut Tree\u7b97\u6cd5</h2><pre><code>#include&lt;iostream&gt;\n#include&lt;cstring&gt;\n#include&lt;cstdio&gt;\n#include&lt;cstring&gt;\nusing namespace std;\nstruct node \n{\n    int data,rev,sum;\n    node *son[2],*pre;\n    bool judge();\n    bool isroot();\n    void pushdown();\n    void update();\n    void setson(node *child,int lr);\n}lct[233];\nint top,a,b;\nnode *getnew(int x)\n{\n    node *now=lct+ ++top;\n    now-&gt;data=x;\n    now-&gt;pre=now-&gt;son[1]=now-&gt;son[0]=lct;\n    now-&gt;sum=0;\n    now-&gt;rev=0;\n    return now;\n}\nbool node::judge(){return pre-&gt;son[1]==this;}\nbool node::isroot()\n{\n    if(pre==lct)return true;\n    return !(pre-&gt;son[1]==this||pre-&gt;son[0]==this);\n}\nvoid node::pushdown()\n{\n    if(this==lct||!rev)return;\n    swap(son[0],son[1]);\n    son[0]-&gt;rev^=1;\n    son[1]-&gt;rev^=1;\n    rev=0;\n}\nvoid node::update(){sum=son[1]-&gt;sum+son[0]-&gt;sum+data;}\nvoid node::setson(node *child,int lr)\n{\n    this-&gt;pushdown();\n    child-&gt;pre=this;\n    son[lr]=child;\n    this-&gt;update();\n}\nvoid rotate(node *now)\n{\n    node *father=now-&gt;pre,*grandfa=father-&gt;pre;\n    if(!father-&gt;isroot()) grandfa-&gt;pushdown();\n    father-&gt;pushdown();now-&gt;pushdown();\n    int lr=now-&gt;judge();\n    father-&gt;setson(now-&gt;son[lr^1],lr);\n    if(father-&gt;isroot()) now-&gt;pre=grandfa;\n    else grandfa-&gt;setson(now,father-&gt;judge());\n    now-&gt;setson(father,lr^1);\n    father-&gt;update();now-&gt;update();\n    if(grandfa!=lct) grandfa-&gt;update();\n}\nvoid splay(node *now)\n{\n    if(now-&gt;isroot())return;\n    for(;!now-&gt;isroot();rotate(now))\n    if(!now-&gt;pre-&gt;isroot())\n    now-&gt;judge()==now-&gt;pre-&gt;judge()?rotate(now-&gt;pre):rotate(now);\n}\nnode *access(node *now)\n{\n    node *last=lct;\n    for(;now!=lct;last=now,now=now-&gt;pre)\n    {\n        splay(now);\n        now-&gt;setson(last,1);\n    }\n    return last;\n}\nvoid changeroot(node *now)\n{\n    access(now)-&gt;rev^=1;\n    splay(now);\n}\nvoid connect(node *x,node *y)\n{\n    changeroot(x);\n    x-&gt;pre=y;\n    access(x);\n}\nvoid cut(node *x,node *y)\n{\n    changeroot(x);\n    access(y);\n    splay(x);\n    x-&gt;pushdown();\n    x-&gt;son[1]=y-&gt;pre=lct;\n    x-&gt;update();\n}\nint query(node *x,node *y)\n{\n    changeroot(x);\n    node *now=access(y);\n    return now-&gt;sum;\n}\nint main()\n{\n    scanf(&quot;%d%d&quot;,&a,&b);\n    node *A=getnew(a);\n    node *B=getnew(b);\n    //\u8fde\u8fb9 Link\n        connect(A,B);\n    //\u65ad\u8fb9 Cut\n        cut(A,B);\n    //\u518d\u8fde\u8fb9orz Link again\n        connect(A,B);\n    printf(&quot;%d\\n&quot;,query(A,B)); \n    return 0;\n}\n</code></pre><hr /><h2>Splay\u7b97\u6cd5</h2><pre><code>//\u4e00\u9897\u8d44\u74f7\u533a\u95f4\u52a0\u3001\u533a\u95f4\u7ffb\u8f6c\u3001\u533a\u95f4\u6c42\u548c\u7684Splay\n#include &lt;bits/stdc++.h&gt;\n#define ll long long\n#define N 100000\nusing namespace std;\nint sz[N], rev[N], tag[N], sum[N], ch[N][2], fa[N], val[N];\nint n, m, rt, x;\nvoid push_up(int x){\n    sz[x] = sz[ch[x][0]] + sz[ch[x][1]] + 1;\n    sum[x] = sum[ch[x][1]] + sum[ch[x][0]] + val[x];\n}\nvoid push_down(int x){\n    if(rev[x]){\n        swap(ch[x][0], ch[x][1]);\n        if(ch[x][1]) rev[ch[x][1]] ^= 1;\n        if(ch[x][0]) rev[ch[x][0]] ^= 1;\n        rev[x] = 0;\n    }\n    if(tag[x]){\n        if(ch[x][1]) tag[ch[x][1]] += tag[x], sum[ch[x][1]] += tag[x];\n        if(ch[x][0]) tag[ch[x][0]] += tag[x], sum[ch[x][0]] += tag[x];\n        tag[x] = 0;\n    }\n}\nvoid rotate(int x, int &k){\n    int y = fa[x], z = fa[fa[x]];\n    int kind = ch[y][1] == x;\n    if(y == k) k = x;\n    else ch[z][ch[z][1]==y] = x;\n    fa[x] = z; fa[y] = x; fa[ch[x][!kind]] = y;\n    ch[y][kind] = ch[x][!kind]; ch[x][!kind] = y;\n    push_up(y); push_up(x);\n}\nvoid splay(int x, int &k){\n    while(x != k){\n        int y = fa[x], z = fa[fa[x]];\n        if(y != k) if(ch[y][1] == x ^ ch[z][1] == y) rotate(x, k);\n        else rotate(y, k);\n        rotate(x, k);\n    }\n}\nint kth(int x, int k){\n    push_down(x);\n    int r = sz[ch[x][0]]+1;\n    if(k == r) return x;\n    if(k &lt; r) return kth(ch[x][0], k);\n    else return kth(ch[x][1], k-r);\n}\nvoid split(int l, int r){\n    int x = kth(rt, l), y = kth(rt, r+2);\n    splay(x, rt); splay(y, ch[rt][1]);\n}\nvoid rever(int l, int r){\n    split(l, r);\n    rev[ch[ch[rt][1]][0]] ^= 1;\n}\nvoid add(int l, int r, int v){\n    split(l, r);\n    tag[ch[ch[rt][1]][0]] += v;\n    val[ch[ch[rt][1]][0]] += v;\n    push_up(ch[ch[rt][1]][0]);\n}\nint build(int l, int r, int f){\n    if(l &gt; r) return 0;\n    if(l == r){\n        fa[l] = f;\n        sz[l] = 1;\n        return l;\n    }\n    int mid = l + r &gt;&gt; 1;\n    ch[mid][0] = build(l, mid-1, mid);\n    ch[mid][1] = build(mid+1, r, mid);\n    fa[mid] = f;\n    push_up(mid);\n    return mid;\n}\nint asksum(int l, int r){\n    split(l, r);\n    return sum[ch[ch[rt][1]][0]];\n}\nint main(){\n    //\u603b\u5171\u4e24\u4e2a\u6570\n    n = 2;\n    rt = build(1, n+2, 0);//\u5efa\u6811\n    for(int i = 1; i &lt;= n; i++){\n        scanf(&quot;%d&quot;, &x);\n        add(i, i, x);//\u533a\u95f4\u52a0\n    }\n    rever(1, n);//\u533a\u95f4\u7ffb\u8f6c\n    printf(&quot;%d\\n&quot;, asksum(1, n));//\u533a\u95f4\u6c42\u548c\n    return 0;\n}\n</code></pre><hr />",
